import React from 'react';
import PropTypes from 'prop-types';
//UI import 
import { black, red, grey, grey2, white } from '../common/colors'
import { level1, level2, level3, level4 } from '../common/fonts'
//Component
const Button = (props) => {
    const { children, type, disabled, theme, ...other } = props;
    return (
        <button type={type} disabled={disabled} {...other} >
            {children}
            <style jsx>
                {`{
                    button {
                        padding: 0.7e, 1.7 em;
                        border: none;
                        border-radius: 3px;
                        text-Transform: uppercase;
                        font-family: 'Popspin';
                        cursor: pointer;
                        outline: none;
                        font-size: 16px;
                        color: ${theme !== 'none' ? white : black};
                        background-color: ${theme === 'red' ? red : theme === 'grey' ? grey : white};
                        box-shadow: ${theme !== 'none' ? level2 : 'none'}; 
                    }
                    button:hover {
                        box-shadow: ${level3};
                    }
                    button:active {
                        box-shadow: ${level4}
                    }
                    button:disable {
                        Color: ${white};
                        box-shadow: ${level1};
                        background-color: ${grey2};
                    }
                }`}
            </style>
        </button>
    )
}
// Component properties
Button.propTypes = {
    type: PropTypes.string,
    disabled: PropTypes.bool,
    theme: PropTypes.string,
};
Button.defaultProps = {
    type: 'button'
}
export default Button;
