import PropTypes from 'prop-types';
//UI import 
import { black, red, white } from '../common/colors'
import {level1, level2, level3, level4 } from '../common/fonts'
//Component
const Button = (props) =>{
    const { children, type, disabled, theme, ...other} = props;
    return (
        <button type={type} disabled={disabled} {...other} >
            {children}
            <style jsx>
                {`{
                    button {
                        padding: 0.7e, 1.7 em;
                        border: none;
                        border-radius: 3px;
                        text-Transform: uppercase;
                        font-family: 'Popspin';
                        cursor: pointer;
                        outline: none;
                        font-size: 16px;
                        color: ${theme !== 'none' ? white  : black}
                    }
                    button:hover {
                        box-shadow: ${level3};
                    }
                    button:active {
                        box-shadow: ${level4}
                    }
                    button:disable {
                        Color: $
                    }
                }`}
            </style>
        </button>
    )
}
Button.propTypes = {

};

export default Button;
